import React from 'react';
import classes from './InitGame.module.css'
const initGame = (props) => {
    let players =[], error=null, changeName = props.changeName;
    if (props.error) error = <p className={classes.error}>{props.error}</p>
    for (let i = 0; i < props.count; i++) {
        players[i] = (
            <div className={classes.player}>
                <label htmlFor="">
                    <p>Player {i + 1}'s name</p><input type="text" onChange={(event) => this.changeName(i)}/>
                </label>
            </div>
        )
    }
    return (
        <div className={classes.container}>
            {players}
            {error}
        </div>
    )
}

export default initGame;
