import React from 'react';
import classes from './InitGame.module.css'
const initGame = (props) => {
    let players = props.players, error=null;
    const initName = (event, i) => {

    }
    if (props.error) error = <p className={classes.error}>{props.error}</p>
    for (let i = 0; i < props.count; i++) {
        players[i] = (
            <div className={classes.player}>
                <label htmlFor="">
                    <p>Player {i + 1}'s name</p><input type="text" onChange={initName.bind(this, event, i)}/>
                </label>
            </div>
        )
    }
    return (
        <div className={classes.container}>
            {initPlayers}
            {error}
        </div>
    )
}

export default initGame;
