import React from 'react';

import classes from './Player.module.css'
import cardIcon from './gaming.svg'
import avatars from './avatar/avatar.js'


const player = (props) => {
    /* Random avatar allocate */
    for (let i = avatars.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [avatars[i], avatars[j]] = [avatars[j], avatars[i]];
    }
    let n = 0;
    const avatar = avatars[n];
    return (
        <div className={classes.player}>
            <img src={avatars[n++]} alt="Avatar" className={classes.avatar} />
            <div className={classes.name}>{props.name}</div>
            <div>
                <img className={classes.icon} src={cardIcon} alt="Cards" />
                <p className={classes.text}>{props.cardNumber}</p>
            </div>
            <div className={classes.score}>{props.score}</div>
        </div>
    )
}

export default player;
